package com.jk.risk.common.domain.enumeration;/** * User by allen * Date 2017/11/9. * 首次付款方式 */public enum FirstPayType {    ZERO(0, "押一付零"), ONE(1, "押一付一"), TWO(2, "压一付二");    private int value;    private String description;    FirstPayType(int value, String description){        this.value = value;        this.description = description;    }    public int getValue() {        return value;    }    public String getDescription() {        return description;    }}