package com.jk.risk.conf;import com.jk.risk.interceptor.RiskInterceptor;import org.springframework.context.annotation.Configuration;import org.springframework.web.servlet.config.annotation.InterceptorRegistry;import org.springframework.web.servlet.config.annotation.WebMvcConfigurerAdapter;/** * 配置拦截器，可配置多个拦截器，做一些安全校验，身份验证等 * User: allen * Date: 2017/11/16 上午10:21 */@Configurationpublic class ConfigurerAdapter extends WebMvcConfigurerAdapter {    @Override    public void addInterceptors(InterceptorRegistry registry) {        registry.addInterceptor(new RiskInterceptor()).addPathPatterns("/**");    }}