package com.jk.risk.util;import java.math.BigDecimal;import java.time.ZoneOffset;/** * 工具类 * User: allen * Date: 2017/11/7 下午10:30 */public class CommonUtil {    private final static int SCALE = 4;    public final static ZoneOffset DEFAULT_ZONE_OFFSET = ZoneOffset.ofHours(8);    public static boolean doubleEqual(double a, double b) {        BigDecimal b1 = new BigDecimal(Double.toString(a));        BigDecimal b2 = new BigDecimal(Double.toString(b));        double less = b1.subtract(b2).doubleValue();        return less == 0;    }    public static double doubleSubtract(double a, double b) {        BigDecimal b1 = new BigDecimal(Double.toString(a));        BigDecimal b2 = new BigDecimal(Double.toString(b));        return b1.subtract(b2).doubleValue();    }    public static double doubleMultiply(double a, double b) {        BigDecimal b1 = new BigDecimal(Double.toString(a));        BigDecimal b2 = new BigDecimal(Double.toString(b));        return b1.multiply(b2).doubleValue();    }    public static double doubleAdd(double a, double... b) {        double result;        BigDecimal b1 = new BigDecimal(Double.toString(a));        for(double f : b){            BigDecimal b2 = new BigDecimal(Double.toString(f));            b1 = b1.add(b2);        }        result = b1.doubleValue();        return result;    }    public static double doubleSubtract(double a, double... b) {        double result ;        BigDecimal b1 = new BigDecimal(Double.toString(a));        for(double f : b){            BigDecimal b2 = new BigDecimal(Double.toString(f));            b1 = b1.subtract(b2);        }        result = b1.doubleValue();        return result;    }    public static void main(String[] args) {    }}